import { Component, Input, Output, EventEmitter, ChangeDetectionStrategy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Product } from '../../../core/services/product.service';

@Component({
  selector: 'app-product-table',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './product-table.component.html',
  styleUrls: ['./product-table.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ProductTableComponent {
  @Input() products: Product[] = [];
  @Input() isLoading = false;
  @Output() edit = new EventEmitter<Product>();
  @Output() delete = new EventEmitter<string>();

  onEdit(product: Product) {
    this.edit.emit(product);
  }

  onDelete(id: string | undefined) {
    if (id && confirm('Are you sure you want to delete this product?')) {
      this.delete.emit(id);
    }
  }

  trackByProductId(index: number, product: Product): string | undefined {
    return product._id;
  }
}
<div class="table-container">
  <div *ngIf="isLoading" class="loading">Loading...</div>

  <div *ngIf="!isLoading && products.length === 0" class="no-data">
    No products found
  </div>

  <table *ngIf="!isLoading && products.length > 0" class="products-table">
    <thead>
      <tr>
        <th>Name</th>
        <th>Description</th>
        <th>Price</th>
        <th>Quantity</th>
        <th>Category</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of products; trackBy: trackByProductId">
        <td>{{ product.name }}</td>
        <td>{{ product.description }}</td>
        <td>${{ product.price | number: '1.2-2' }}</td>
        <td>{{ product.quantity }}</td>
        <td>{{ product.category }}</td>
        <td class="actions">
          <button class="btn-edit" (click)="onEdit(product)">Edit</button>
          <button class="btn-delete" (click)="onDelete(product._id)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
