import { Component, Input, Output, EventEmitter, ChangeDetectionStrategy, OnChanges, SimpleChanges } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Product } from '../../../core/services/product.service';

@Component({
  selector: 'app-product-form',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './product-form.component.html',
  styleUrls: ['./product-form.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ProductFormComponent implements OnChanges {
  @Input() product: Product | null = null;
  @Input() isSubmitting = false;
  @Output() submit = new EventEmitter<Product>();
  @Output() cancel = new EventEmitter<void>();

  form: FormGroup;
  categories = ['Electronics', 'Clothing', 'Food', 'Books', 'Other'];

  constructor(private fb: FormBuilder) {
    this.form = this.createForm();
  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes['product'] && this.product) {
      this.form.patchValue(this.product);
    }
  }

  private createForm(): FormGroup {
    return this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      description: ['', [Validators.required, Validators.minLength(10)]],
      price: ['', [Validators.required, Validators.min(0)]],
      quantity: ['', [Validators.required, Validators.min(0)]],
      category: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.form.valid) {
      this.submit.emit(this.form.value);
    }
  }

  onCancel() {
    this.form.reset();
    this.cancel.emit();
  }

  getErrorMessage(fieldName: string): string {
    const control = this.form.get(fieldName);
    if (!control || !control.errors) return '';

    if (control.errors['required']) return `${fieldName} is required`;
    if (control.errors['minlength']) return `${fieldName} must be at least ${control.errors['minlength'].requiredLength} characters`;
    if (control.errors['min']) return `${fieldName} must be greater than 0`;

    return '';
  }
}
<form [formGroup]="form" (ngSubmit)="onSubmit()" class="product-form">
  <h2>{{ product ? 'Edit Product' : 'Add New Product' }}</h2>

  <div class="form-group">
    <label for="name">Product Name *</label>
    <input
      id="name"
      type="text"
      formControlName="name"
      placeholder="Enter product name"
      class="form-input"
      [class.error]="form.get('name')?.invalid && form.get('name')?.touched"
    />
    <span class="error-message" *ngIf="form.get('name')?.invalid && form.get('name')?.touched">
      {{ getErrorMessage('name') }}
    </span>
  </div>

  <div class="form-group">
    <label for="description">Description *</label>
    <textarea
      id="description"
      formControlName="description"
      placeholder="Enter product description"
      class="form-input"
      rows="4"
      [class.error]="form.get('description')?.invalid && form.get('description')?.touched"
    ></textarea>
    <span class="error-message" *ngIf="form.get('description')?.invalid && form.get('description')?.touched">
      {{ getErrorMessage('description') }}
    </span>
  </div>

  <div class="form-row">
    <div class="form-group">
      <label for="price">Price *</label>
      <input
        id="price"
        type="number"
        formControlName="price"
        placeholder="0.00"
        class="form-input"
        min="0"
        step="0.01"
        [class.error]="form.get('price')?.invalid && form.get('price')?.touched"
      />
      <span class="error-message" *ngIf="form.get('price')?.invalid && form.get('price')?.touched">
        {{ getErrorMessage('price') }}
      </span>
    </div>

    <div class="form-group">
      <label for="quantity">Quantity *</label>
      <input
        id="quantity"
        type="number"
        formControlName="quantity"
        placeholder="0"
        class="form-input"
        min="0"
        [class.error]="form.get('quantity')?.invalid && form.get('quantity')?.touched"
      />
      <span class="error-message" *ngIf="form.get('quantity')?.invalid && form.get('quantity')?.touched">
        {{ getErrorMessage('quantity') }}
      </span>
    </div>
  </div>

  <div class="form-group">
    <label for="category">Category *</label>
    <select
      id="category"
      formControlName="category"
      class="form-input"
      [class.error]="form.get('category')?.invalid && form.get('category')?.touched"
    >
      <option value="">Select a category</option>
      <option *ngFor="let cat of categories" [value]="cat">{{ cat }}</option>
    </select>
    <span class="error-message" *ngIf="form.get('category')?.invalid && form.get('category')?.touched">
      {{ getErrorMessage('category') }}
    </span>
  </div>

  <div class="form-actions">
    <button type="submit" class="btn-submit" [disabled]="form.invalid || isSubmitting">
      {{ isSubmitting ? 'Saving...' : (product ? 'Update Product' : 'Add Product') }}
    </button>
    <button type="button" class="btn-cancel" (click)="onCancel()" [disabled]="isSubmitting">
      Cancel
    </button>
  </div>
</form>
